create database TwoEng;
use TwoEng;
create table NguoiDung (UserID char(10), Password char(200), Firstname nvarchar(30), Lastname nvarchar(10), Email varchar(50), GioiTinh nvarchar(5), NgaySinh varchar(10), DiaChi nvarchar(100), SDT varchar(15), LoaiTK char(2), code char(6));
create table DangKi (STT int AUTO_INCREMENT primary key, HoTen nvarchar(100), SoDienThoai char(11), Email varchar(50), CMND char(10), PhuongThucDongHocPhi nvarchar(20), Lop nvarchar(50), onlineORoffline nvarchar(15), Ca varchar(10), Thu varchar(10), NgayKhaiGiang varchar(11), TinhTrang int);
create table KhaiGiang (STT int AUTO_INCREMENT primary key, MaKhoaHoc char(10), MaLop char(20), TenKhoaHoc nvarchar(50), Ca varchar(15), Thu varchar(10), Ngay varchar(15), Loai nvarchar(20), TinhTrang int);
create table Lop (MaLop char(20) primary key, TenLop nvarchar(50), MatKhau char(200), TinhTrang int);
create table BaiTapLop (STT int AUTO_INCREMENT primary key, MaLop char(20), YeuCau nvarchar(500), MoTa nvarchar(500), Filename nvarchar(50));
create table BaiNop (STT int AUTO_INCREMENT primary key, STTBai int, MaLop char(20), MaHV char(10), Filename nvarchar(50));
create table XepLop (STT int AUTO_INCREMENT primary key, MaLop char(20), MaHV char(10));
create table Sach (STT int AUTO_INCREMENT primary key, MaSach char(40), TenSach nvarchar(200), Hinh varchar(200), Gia int, MoTa nvarchar(1000));
create table GiangDay (STT int AUTO_INCREMENT primary key, MaGV char(10), MaLop char(20));
create table LichSuTest (STT int AUTO_INCREMENT primary key, UserID char(10), TenBaiTest nvarchar(100), TongSoCau int, SoCauDung int, ThoiGianTest nvarchar(30));
create table DatHang (STT int AUTO_INCREMENT primary key, MaSach char(40), TenSach nvarchar(200), DonGia nvarchar(20), SoLuong int, HoTen nvarchar(100), SoDienThoai char(20), Email varchar(100), DiaChi nvarchar(500), HinhThucThanhToan nvarchar(200), TongTienHang nvarchar(50), PhiVanChuyen nvarchar(10), ThanhTien nvarchar(50), TinhTrang int);



















